  //remove item from cart and check that the redirect is made
  clickOnDeleteItemOnCart() {
    browser.pause(1000);
    const deleteButton = $(".fa-trash-o");
    deleteButton.click();
    browser.pause(2000);
    currentUrl = browser.getUrl();
    assert.equal(currentUrl, "https://www.bitdefender.com/solutions/");
  },
